    @Test
    public void removeDuplicates() {
        input  = new int[]{3,3,3,3,3,3,3};
        System.out.println(Arrays.toString(input));
        int i = solution.removeDuplicates(input);
        System.out.print("[");
        for (int j = 0; j < i; j++) {
            System.out.print(input[j]+",");
        }
        System.out.println("]");

    }

    @Test
    public void removeElement() {
        int val = 2;
        input= new int[]{0,1,2,2,3,0,4,2,2};
        int element = solution.removeElement(input, val);
        System.out.println(element);
        for (int i = 0; i < element; i++) {
            System.out.print(input[i]+",");
        }

    }

    @Test
    public void sortColors() {
        int len = 0;
        for (int i = 4; i < 6; i++) {
            len = (int) Math.pow(2,i);
            input = new int[len];
            for (int j = 0; j < len; j++) {
                input[j] = (int) (Math.random() * 3);
            }
            System.out.println(Arrays.toString(input));
            solution.sortColors(input);
            System.out.println(Arrays.toString(input));
        }


        
    }


